import bgimg from '../asset/bgimg.jpg';
import React from "react";
import '../App.css';
import { useState } from 'react';

function Sample(props) {
  const details = {
  salary: 18300,
  earning: 212.28,
  fees: 21.23
}




const [salaryDetail, salaryUpdate] = useState(details);





   const min = 500;
  const max = 250000;
  const earningDivisor = 86.20;
  const feesDivisor = 861.98;


  const handleChange = (e) =>{
    var value = parseInt(e.target.value.replace(/,/g, ''));    
    value = value > max ? max : value;    
    const isNaN = !Number.isNaN(parseFloat(value));    
    salaryUpdate({
      salary: isNaN ? parseFloat(value) : 0,
      earning: isNaN ? parseFloat(value / earningDivisor).toFixed(2) : 0,
      fees: isNaN ? parseFloat(value / feesDivisor).toFixed(2) : 0
    })
  }




  return (
    <div className="container">
      <section class="generic-banner relative banner-area-inner26">
        <div>
          <h1 className="big-heading">Lending made easy</h1>
          <p className="sub-heading">Earn up to -1.16% a year across a wide range of P2P sites with investUP</p>
          <div className="btn">Get Started</div>
        </div>
      </section>
       {/* <div className="view">
      <section class="generic-banner relative banner-area-inner26">
				<div class="overlay overlay-bg overlay-bg-blk"></div>
				<div class="container">
					<div class="row height align-items-center justify-content-center">
						<div class="col-lg-10">
							<div class="generic-banner-content">
								<h2 class="head2-inner">FAQ</h2>
								<p class="text-white" style={{opacity: 0.5}}> B1DESKTM comprises of a robust maturity assessment methodology and transformation cookbooks to progressively drive the Service Desk transformation to the desired end-state.</p>
							</div>							
						</div>
					</div>
				</div>
			</section>        
      </div> */}
      <div className="main-content">
        <div className="form-text">
          <div style={{ fontSize: "37px", textAlign: "center" }}>With<span className="highlight-text salary">€<input min={min} max={max} value={salaryDetail.salary.toLocaleString()} onChange={(e)=>handleChange(e)}></input></span> your estimated earning cound be <span className="earnings highlight-text">€-{salaryDetail.earning.toLocaleString()}</span> * per
  year with fees of just <span className="highlight-text fees">€-{salaryDetail.fees.toLocaleString()}</span> per year.
  </div>
          <p className="paragraph">Estimated earnings are based on -1.16% per annum before tax any potential losses. As with most forms of investment, peer-to-peer lending carries a
    degree of risk to your capital; In this case, if borrows were unable to repay your loans.</p>
        </div>
        <div className="range-value">
          <span className="start-value">€ 500</span>
          <input id="range" type="range" min={min} max={max} value={salaryDetail.salary} onChange={(e) => handleChange(e)} step="1" />
          <span className="end-value">€ 250,000</span>
        </div>
      </div>
    </div>
  );
}


export default Sample;
